package net.zdsoft.chnmaster.service;import com.alibaba.fastjson.JSONObject;/** * QQ第三方登录 * * @author * */public interface QQService {    /**     * 获取qqAccessToken和OpenId     *     * @param code     * @param redirectUrl     *            回调url @return{'accessToken':'','openId':''     */    public JSONObject getAccessTokenAndOpenId(String code, String redirectUrl) throws Exception;    /**     * 获取用户信息     *     * @param openId     * @param accessToken     * @param isAppQQLogin     *            @return{'gender':"男/女",'nickname':'昵称','figureurl_qq_2':'qq图片地址'...     */    public JSONObject getUserInfo(String openId, String accessToken, boolean isAppQQLogin) throws Exception;    /**     * 获取用户信息(web和wap方式)     *     * @param openId     * @param accessToken     * @param isAppQQLogin     *            @return{'gender':"男/女",'nickname':'昵称','figureurl_qq_2':'qq图片地址'...     */    public JSONObject getUserInfo(String openId, String accessToken) throws Exception;}