package net.zdsoft.common.entity.index.mapper;import java.sql.ResultSet;import java.sql.SQLException;import org.springframework.jdbc.core.RowMapper;import net.zdsoft.common.entity.index.IndexAdv;import net.zdsoft.common.enums.ContentType;import net.zdsoft.common.enums.FieldType;import net.zdsoft.common.enums.YesNoType;/** * @author hongx */@SuppressWarnings("rawtypes")public class IndexAdvRowMapper implements RowMapper {    private static IndexAdvRowMapper rowMapper = new IndexAdvRowMapper();    public static IndexAdvRowMapper instance() {        return rowMapper;    }    @Override    public Object mapRow(ResultSet rs, int i) throws SQLException {        IndexAdv adv = new IndexAdv();        adv.setId(rs.getLong("ID"));        adv.setCompeteId(rs.getLong("COMPETE_ID"));        adv.setAdvSource(rs.getInt("ADV_SOURCE"));        adv.setTitle(rs.getString("TITLE"));        adv.setFieldType(FieldType.get(rs.getInt("FIELD_TYPE")));        adv.setAdvTypeId(rs.getLong("ADV_TYPE_ID"));        adv.setContentType(ContentType.get(rs.getInt("CONTENT_TYPE")));        adv.setLinkUrl(rs.getString("LINK_URL"));        adv.setLocalFile(rs.getString("LOCAL_FILE"));        adv.setIsShow(YesNoType.get(rs.getInt("IS_SHOW")));        adv.setPictureFile(rs.getString("PICTURE_FILE"));        adv.setDisplayOrder(rs.getInt("DISPLAY_ORDER"));        adv.setCreateDate(rs.getTimestamp("CREATE_DATE"));        adv.setRemark(rs.getString("REMARK"));        return adv;    }}