package net.zdsoft.common.entity.system;import net.zdsoft.common.entity.BaseEntity;/** * 行政区划表【t_system_region】 * * @author * */public class SystemRegion extends BaseEntity {    private static final long serialVersionUID = 1L;    /**     * 行政区划码     */    private String regionCode;    /**     * 区划名称     */    private String regionName;    /**     * 行政区划全码     */    private String fullCode;    /**     * 区划全名称     */    private String fullName;    /**     * 全名称简拼     */    private String shortSpell;    /**     * 设置行政区划码     */    public void setRegionCode(String regionCode) {        this.regionCode = regionCode;    }    /**     * 获取行政区划码     */    public String getRegionCode() {        return this.regionCode;    }    /**     * 设置区划名称     */    public void setRegionName(String regionName) {        this.regionName = regionName;    }    /**     * 获取区划名称     */    public String getRegionName() {        return this.regionName;    }    /**     * 设置行政区划全码     */    public void setFullCode(String fullCode) {        this.fullCode = fullCode;    }    /**     * 获取行政区划全码     */    public String getFullCode() {        return this.fullCode;    }    /**     * 设置区划全名称     */    public void setFullName(String fullName) {        this.fullName = fullName;    }    /**     * 获取区划全名称     */    public String getFullName() {        return this.fullName;    }    public String getShortSpell() {        return shortSpell;    }    public void setShortSpell(String shortSpell) {        this.shortSpell = shortSpell;    }}