package net.zdsoft.common.entity.wxb.mapper;import java.sql.ResultSet;import java.sql.SQLException;import org.springframework.jdbc.core.RowMapper;import net.zdsoft.common.entity.wxb.WxbLog;import net.zdsoft.common.enums.CourseContentType;import net.zdsoft.common.enums.WxbLogUserType;/** * 无限宝服务器【t_wxb】 * * @author * */@SuppressWarnings("rawtypes")public class WxbLogRowMapper implements RowMapper {    private static WxbLogRowMapper rowMapper = new WxbLogRowMapper();    public static IdRowMapper idRowMapper = new IdRowMapper();    public static WxbLogStatRowMapper statRowMapper = new WxbLogStatRowMapper();    public static WxbLogStat2RowMapper stat2RowMapper = new WxbLogStat2RowMapper();    public static WxbLogRowMapper instance() {        return rowMapper;    }    static class IdRowMapper implements RowMapper<Long> {        @Override        public Long mapRow(ResultSet rs, int i) throws SQLException {            return rs.getLong("id");        }    }    static class WxbLogStatRowMapper implements RowMapper {        @Override        public WxbLog mapRow(ResultSet rs, int i) throws SQLException {            WxbLog wb = (WxbLog) rowMapper.mapRow(rs, i);            wb.setCourseName(rs.getString("COURSE_NAME"));            wb.setCourseContentType(CourseContentType.get(rs.getInt("CONTENT_TYPE")));            wb.setBeginTime(rs.getTimestamp("BEGINTIME"));            wb.setEndTime(rs.getTimestamp("ENDTIME"));            return wb;        }    }    static class WxbLogStat2RowMapper implements RowMapper {        @Override        public WxbLog mapRow(ResultSet rs, int i) throws SQLException {            WxbLog wb = new WxbLog();            wb.setCourseName(rs.getString("COURSE_NAME"));            wb.setCourseContentType(CourseContentType.get(rs.getInt("CONTENT_TYPE")));            wb.setRealName(rs.getString("REALNAME"));            wb.setUserId(rs.getLong("USERID"));            float second = rs.getFloat("CHOUR") / 3600;            wb.setCountHour(second);            wb.setSeq(rs.getInt("SEQ"));            wb.setBeginTime(rs.getTimestamp("BEGINTIME"));            wb.setEndTime(rs.getTimestamp("ENDTIME"));            return wb;        }    }    @Override    public Object mapRow(ResultSet rs, int i) throws SQLException {        WxbLog log = new WxbLog();        log.setId(rs.getLong("ID"));        log.setWxbId(rs.getLong("WXBID"));        log.setCourseId(rs.getLong("COURSEID"));        log.setUserId(rs.getLong("USERID"));        log.setRealName(rs.getString("REALNAME"));        log.setUserType(WxbLogUserType.get(rs.getInt("USERTYPE")));        log.setLoginTime(rs.getTimestamp("LOGINTIME"));        log.setLogoutTime(rs.getTimestamp("LOGOUTTIME"));        log.setCreateTime(rs.getTimestamp("CREATETIME"));        log.setRemark(rs.getString("REMARK"));        log.setSeq(rs.getInt("SEQ"));        log.setLoginflag(rs.getString("LOGINFLAG"));        log.setNoSeriousTime(rs.getInt("NO_SERIOUS_TIME"));        return log;    }}