/*  * @(#)NetStudyPropertyPlaceholderConfigurer.java    Created on 2013-11-6 * Copyright (c) 2013 ZDSoft Networks, Inc. All rights reserved. * $Id$ */package net.zdsoft.common.resource;import org.springframework.beans.factory.config.PropertyPlaceholderConfigurer;import org.springframework.core.io.Resource;/** * 网校读取配置文件类 * <property name="locations"> *    <list> *        <value>file:/opt/server_data/netstudy/jdbc.properties</value> *        <value>classpath:jdbc.properties</value> *    </list> * </property> * 优先读取file:/opt/server_data/netstudy/jdbc.properties这个文件， 如果这个文件不存在则读取classpath:jdbc.properties * @author shenl * @version $Revision: 1.0 $, $Date: 2013-11-6 下午4:46:24 $ */public class NetStudyPropertyPlaceholderConfigurer extends PropertyPlaceholderConfigurer {    @Override    public void setLocations(Resource[] locations) {        for (Resource resource : locations) {            if (resource.exists()) {                super.setLocations(new Resource[]{resource });                return;            }        }    }}