-- 主键生成函数修改 DROP FUNCTION IF EXISTS fun_next_seq; DELIMITER $$    CREATE FUNCTION fun_next_seq(in_table_name varchar(50))RETURNS int                BEGIN    select upper(in_table_name) into in_table_name;    if in_table_name='T_USER' then        REPLACE INTO t_seq_user (table_name) VALUES (in_table_name);        elseif in_table_name='T_AGENCY' then        REPLACE INTO t_seq_agency (table_name) VALUES (in_table_name);      elseif in_table_name='T_WXB' then        REPLACE INTO t_seq_wxb (table_name) VALUES (in_table_name);     elseif in_table_name='T_SYSTEM_USER' or in_table_name='T_SYSTEM_ROLE' or in_table_name='T_PICTURE' or in_table_name='T_INDEX_ADV' or in_table_name='T_INDEX_STUDENT' then        REPLACE INTO t_seq_common (table_name) VALUES (in_table_name);    else        RETURN 0;    end if;    RETURN LAST_INSERT_ID();    END $$                                         DELIMITER ; -- 申请加盟的基地（高校、机构或琴行）CREATE TABLE T_AGENCY_JOIN(  ID                    INTEGER NOT NULL,   -- ID=T_AGENCY.ID（T_AGENCY序列化中取）  AGENCY_NAME           VARCHAR(75),        -- 基地名称  REGIONCODE            VARCHAR(6) NULL,    -- 所在地区（6位行政区划）  CONTACT_MAN           VARCHAR(100),       -- 联系人  CONTACT_PHONE         VARCHAR(100),       -- 联系电号  EMAIL                 VARCHAR(50),        -- Email  ADDRESS               VARCHAR(100),       -- 详细地址  INTRODUCTION          VARCHAR(4000),      -- 介绍  AGENCY_TYPE           TINYINT DEFAULT 1 NOT NULL,  -- 基地类型（1高校，2培训机构，3琴行）  AGENCY_STATUS         TINYINT DEFAULT 0,  -- 申请加盟状态（0：申请中，1：已加盟）  CREATETIME            DATETIME,  REMARK                VARCHAR(4000),      -- 备注  PRIMARY KEY (ID)) ENGINE=INNODB DEFAULT CHARSET=UTF8;-- 添加字段：基地来源（0后台创建，1申请加盟）alter table T_AGENCY add column AGENCY_SOURCE TINYINT DEFAULT 0 AFTER IS_CANCEL;-- 添加字段：REMARK备注/姓名alter table T_INDEX_ADV add column REMARK VARCHAR(100);-- 修改字段：ID去掉自增alter table T_INDEX_ADV modify ID INTEGER NOT NULL;-- 添加字段：PROGRESS进度状态alter table T_COURSE a