package net.zdsoft.chnmaster.entity.system.mapper;import java.sql.ResultSet;import java.sql.SQLException;import org.springframework.jdbc.core.RowMapper;import net.zdsoft.chnmaster.entity.system.SystemUserRole;/** * 用户角色【t_system_user_role】 *  * @author *  */@SuppressWarnings("rawtypes")public class SystemUserRoleRowMapper implements RowMapper {    private static SystemUserRoleRowMapper rowMapper = new SystemUserRoleRowMapper();    public static SystemUserRoleRowMapper instance() {        return rowMapper;    }    @Override    public Object mapRow(ResultSet rs, int i) throws SQLException {        SystemUserRole systemUserRole = new SystemUserRole();        systemUserRole.setId(rs.getLong("ID"));        systemUserRole.setUserId(rs.getLong("USERID"));        systemUserRole.setRoleId(rs.getLong("ROLEID"));        return systemUserRole;    }}